package com.javaex.basic.types;

public class CharEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			char c1 = 'A';
			char c2 = '한';
			
			System.out.println(c1);
			System.out.println(c2);
			
			System.out.println(c1 + c2);
			
			String str = "A한";
			System.out.println(str);
	}

}
